elements.expression:
filters ungrouped=true
| essql 
  query="SELECT cast (\"source.ip\" AS string) AS IP FROM \"apm*\" where \"processor.event\" = 'transaction' group by \"IP\""
| math "unique(IP)"
| metric {var "label_green_box"} 
  metricFont={font align="right" color="#FFFFFF" family="'Open Sans', Helvetica, Arial, sans-serif" italic=false size=48 underline=false weight="normal"} 
  labelFont={font align="right" color="#FFFFFF" family="'Open Sans', Helvetica, Arial, sans-serif" italic=false size=16 underline=false weight="bold"} metricFormat="0,0.[000]"
| render containerStyle={containerStyle}

var_set "appName" value={
  filters
  | essql query="SELECT service.name FROM \"apm*\" where service.name is not null group by service.name "
  | if condition={rowCount | gt 0} then={getCell row=0 column="service.name"} else={string ""}
}

| filters
| markdown {var "appName"} 
  font={font align="left" color="#444444" family="'Open Sans', Helvetica, Arial, sans-serif" italic=false size=24 underline=false weight="bold"}
| render

var_set "row" value=0
| filters
  | essql query="SELECT service.name, Replace(service.name, ' ' , '%20') as appNameURL FROM \"apm*\" where service.name is not null group by service.name"
  | var_set "appName" value={if condition={rowCount | gt {var "row"}} then={getCell row={var "row"} column="service.name"} else={string ""}}
  | var_set "appNameURL" value={if condition={rowCount | gt {var "row"}} then={getCell row={var "row"} column="appNameURL"} else={string ""}}

| filters
  | essql query={string "SELECT service.name FROM \"apm*\" where service.name = '" {var appName} "' and processor.event = 'error' "}
  | var_set name="count" value={rowCount}
  | var_set name="color" value={var count | if condition={eq 0} then="#AAAAAA" else="#BD271E"}
  | markdown "[" {var "count"} "](/s/" {var kibana_space_id} "/app/dashboards#/view/" {var dashboard_id} "?_g=(filters:!(),refreshInterval:(pause:!t,value:0),time:(from:now-15m,to:now))&_a=(description:'',filters:!(('$state':(store:appState),meta:(alias:!n,disabled:!f,key:service.name,negate:!f,params:(query:" {var appNameURL} "),type:phrase),query:(match_phrase:(service.name:" {var appNameURL} ")))),fullScreenMode:!f,options:(hidePanelTitles:!f,useMargins:!t),query:(language:kuery,query:''),timeRestore:!f,title:Dashboard,viewMode:view))"
| render 
  css={string ".canvasRenderEl {
  position: relative;
}

.canvasRenderEl a {
  background-color: " {var "color"} ";
  display: block;
  margin: 20px;
padding-top: 12px;
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  opacity: 1;
  vertical-align: center;
border-radius: 4px;
line-height: 20px;
text-align: center;
font-size: 20px;
text-decoration: none;
box-shadow: 0 2px 2px rgba(0,0,0,0.2);
color: #FFFFFF;
}

.canvasRenderEl a:after {
content: '" {var label_red_box} "';
font-size: 14px;
display: block;
}"

{var count | if condition={gt 0} then={string "
.canvasRenderEl a:hover {
  background-color: #ff0000;
  transition: all .25s;
}"}}
}

var_set "row" value=0
| filters
  | essql query="SELECT service.name, Replace(service.name, ' ' , '%20') as appNameURL FROM \"apm*\" where service.name is not null group by service.name"
  | var_set "appName" value={if condition={rowCount | gt {var "row"}} then={getCell row={var "row"} column="service.name"} else={string ""}}
  | var_set "appNameURL" value={if condition={rowCount | gt {var "row"}} then={getCell row={var "row"} column="appNameURL"} else={string ""}}

| filters
  | essql query={string "SELECT service.name FROM \"apm*\" where service.name = '" {var appName} "' and processor.event = 'transaction' and transaction.duration.us > 2 "}
  | var_set name="count" value={rowCount }
  | var_set name="color" value={var "count" | if condition={eq 0} then="#AAAAAA" else="#f2bc33"}
  | markdown "[" {var "count"} "](/s/" {var kibana_space_id} "/app/dashboards#/view/" {var dashboard_id} "?_g=(filters:!(),refreshInterval:(pause:!t,value:0),time:(from:now-15m,to:now))&_a=(description:'',filters:!(('$state':(store:appState),meta:(alias:!n,disabled:!f,key:service.name,negate:!f,params:(query:" {var appNameURL} "),type:phrase),query:(match_phrase:(service.name:" {var appNameURL} ")))),fullScreenMode:!f,options:(hidePanelTitles:!f,useMargins:!t),query:(language:kuery,query:''),timeRestore:!f,title:Dashboard,viewMode:view))"
  | render 
  css={string ".canvasRenderEl {
  position: relative;
}

.canvasRenderEl a {
  background-color: " {var "color"} ";
  display: block;
  margin: 20px;
padding-top: 4px;
  position: absolute;
  top: 0;
  bottom: 0;
  right: 0;
  left: 0;
  opacity: 1;
  vertical-align: center;
border-radius: 4px;
line-height: 20px;
text-align: center;
font-size: 20px;
text-decoration: none;
box-shadow: 0 2px 2px rgba(0,0,0,0.2);
color: #FFFFFF;
}

.canvasRenderEl a:after {
content: '" {var label_yellow_box} "';
font-size: 14px;
display: block;
}" {var "count" | if condition={gt 0} then="
.canvasRenderEl a:hover {
  background-color: #fcf003;
  transition: all .25s;
}"}}

